export default {
	myVar1: [],
	myVar2: {},
	search_Customer: () => {
		for(let i= (GET_CALLERSDATA.data.length-1); i>=0;i--){
			if(GET_CALLERSDATA.data[i].PHONE_NUMBER == search_cust.text){
				showModal("LEAD_DATA")
				break;
			}else if(GET_CALLERSDATA.data[i].PHONE_NUMBER != search_cust.text){
				continue;
			}else{
				showAlert("CUSTOMER NOT EXIT")
			}
		}
	},
	activation :()=>{
		for(let i= (GET_CALLERSDATA.data.length-1); i>=0;i--){
			if(GET_CALLERSDATA.data[i].REMARK == "RNR" || GET_CALLERSDATA.data[i].REMARK == "INTERESTED" || GET_CALLERSDATA.data[i].REMARK == "SWITCH OFF/NOT REACHBLE"){
				return true;
			}
		}
	},
	RNR_Active :()=>{
		if(log_remark.selectedOptionValue == "CALL BACK" || remark.selectedOptionValue == "CALL BACK"){
			return true;
		}else if(log_remark.selectedOptionValue == "RNR" || remark.selectedOptionValue == "RNR"){
			return true;
		}else {
			return false;
		}
	},
	RNR_Schd:()=>{
		if(log_remark.selectedOptionValue == "RNR" || remark.selectedOptionValue == "RNR"){
			return helper_js.time();
		}
	},
	Schd :()=>{
			let data=[];
	for (let i=0; i<SELECT_LEADS_CALLBACK.data.length; i++)
	{	
		if(	(SELECT_LEADS_CALLBACK.data[i].SCHEDULE_DATE)<=helper_js.get_today_with_minutes())
		{
			data.push(SELECT_LEADS_CALLBACK.data[i]);
		}
	}
		if(data.length == 0){
			for (let i=0; i<SELECT_LEADS_CALLBACK.data.length; i++){	
		if(	SELECT_LEADS_CALLBACK.data[i].SCHEDULE_DATE =="" || !(SELECT_LEADS_CALLBACK.data[i].SCHEDULE_DATE) ){
			data.push(SELECT_LEADS_CALLBACK.data[i]);
		}}}

		data.sort((a,b)=>{
			return ((b.SCHEDULE_DATE)-(a.SCHEDULE_DATE))
		})
		console.log();
	return data;
	},
	myFun2: async () => {
		//use async-await or promises
	}
}
