export default {
	myVar1: [],
	myVar2: {},
	getTodayData: () => {
		let returnData=[];
		for (let i= (SELECT_LEADS.data.length-1); i>=0;i--)
		{
				if(SELECT_LEADS.data[i].REMARK== "OLD LEADS" && SELECT_LEADS.data[i].ACTIVE==true)
				{
					returnData.push(SELECT_LEADS.data[i])
				}
		}
		return returnData
	},
	get_schedule_data:()=>{
	let data=[];
	for (let i=0; i<SELECT_LEADS_CALLBACK.data.length; i++)
	{	
		if(helper_js.to_date(SELECT_LEADS_CALLBACK.data[i].SCHEDULE_DATE)<=helper_js.get_today())
		{

				data.push(SELECT_LEADS_CALLBACK.data[i]);
		}
	}
		data.sort((a,b)=>{
			return (helper_js.to_date(b.SCHEDULE_DATE)-helper_js.to_date(a.SCHEDULE_DATE))
		})
	return data;
},
	scheduledata:()=>{
	let data=[];
	for (let i=SELECT_LEADS.data.length-1; i>=0; i--)
	{	
		if(helper_js.to_date(SELECT_LEADS.data[i].SCHEDULE_DATE)<=helper_js.get_today())
		{
			if(SELECT_LEADS.data[i].REMARK=="CALL BACK" && SELECT_LEADS.data[i].ACTIVE==true)
			{
				data.push(SELECT_LEADS.data[i]);
			}
		}
	}
		data.sort((a,b)=>{
			return (helper_js.to_date(b.SCHEDULE_DATE)-helper_js.to_date(a.SCHEDULE_DATE))
		})
	return data;
},
	get_followups_data:()=>{
	let data=[];
	for (let i=0; i<SELECT_LEADS.data.length; i++)
	{
		if(SELECT_LEADS.data[i].REMARK=="INTERESTED" || SELECT_LEADS.data[i].REMARK=="RNR" || SELECT_LEADS.data[i].REMARK=="SWITCH OFF/NOT REACHBLE" && SELECT_LEADS.data[i].ACTIVE==true)
		{
			data.push(SELECT_LEADS.data[i]);
		}
	}
	return data;
},
	get_request_data:()=>{
	let data=[];
	for (let i=0; i<SELECT_LEADS.data.length; i++)
	{                                        
			if(SELECT_LEADS.data[i].REMARK=="LIVE DATA" && SELECT_LEADS.data[i].ACTIVE==true)
			{
				data.push(SELECT_LEADS.data[i]);
			}
	}
	return data;
	},
	pegereload:()=>{
		storeValue("current_lead_card",0);
		storeValue("table",query_js.getTodayData());
		storeValue("current_demo_card",0);
		storeValue("table",query_js.get_request_data())
		storeValue("current_call_back_card",0);
		storeValue("table",query_js.get_schedule_data())
		storeValue("current_followup_card",0);
		storeValue("table",query_js.get_followups_data())
	},
	next:()=>{
/*
		storeValue("current_lead_card",0) 
		storeValue("current_demo_card",0) 
		storeValue("current_call_back_card",0) 
		return
*/
		switch(appsmith.store.table_num)
			{
				case 0:
					if(appsmith.store.current_lead_card>=appsmith.store.table.length-1)
						{
							SELECT_LEADS.run();
							showAlert("Click OLD leads button to Confirm","success")
							return appsmith.store.current_lead_card
						}
					storeValue("current_lead_card",appsmith.store.current_lead_card+1)
					return appsmith.store.current_lead_card
					break;
				case 1:
						if(appsmith.store.current_demo_card>=appsmith.store.table.length-1)
						{
							SELECT_LEADS.run();
							showAlert("Click Live Data button to Confirm","success")
							return appsmith.store.current_demo_card
						}
					storeValue("current_demo_card",appsmith.store.current_demo_card+1)
					return appsmith.store.current_demo_card
					break;
				case 2:
					if(appsmith.store.current_call_back_card>=appsmith.store.table.length-1)
						{
							SELECT_LEADS.run();
							showAlert("Click Callbacks button to Confirm","success")
							return appsmith.store.current_call_back_card
						}
					storeValue("current_call_back_card",appsmith.store.current_call_back_card+1)
					return appsmith.store.current_call_back_card
					break;
				case 3:
					if(appsmith.store.current_followup_card>=appsmith.store.table.length-1)
						{
							SELECT_LEADS.run();
							showAlert("Click Followups button to confirm","success")
							return appsmith.store.current_followup_card
						}
					storeValue("current_followup_card",appsmith.store.current_followup_card+1)
					return appsmith.store.current_followup_card
					break;
				case 4:
					showAlert("Data updated","success")
					break
				default: 
					return Error()
			}

	},
	copy:()=>{
		let data="DATE: "+appsmith.store.selectedTableRow.SCHEDULE_DATE+"\nCALLER: "+appsmith.store.selectedTableRow.CALLER+"\nNAME: "+appsmith.store.selectedTableRow.NAME+"\nPHONE: "+appsmith.store.selectedTableRow.PHONE_NUMBER+"\nADDRESS: "+appsmith.store.selectedTableRow.ADDRESS+"\nALTERNATE NUMBER: "+appsmith.store.selectedTableRow.ALTERNATE_NUMBER+"\nLOCATION:"+appsmith.store.selectedTableRow.LOCATION
		showAlert("copied","success")
		return data
	},
	return_current_card:()=>
	{
		if(appsmith.store.table_num==0)
			{
				return appsmith.store.table[appsmith.store.current_lead_card]
			}
		else if(appsmith.store.table_num==1)
			{
				return appsmith.store.table[appsmith.store.current_demo_card]
			}
		else if(appsmith.store.table_num==2)
			{
				return appsmith.store.table[appsmith.store.current_call_back_card]
			}
		else if(appsmith.store.table_num==3)
			{
				return appsmith.store.table[appsmith.store.current_followup_card]
			}
		return appsmith.store.table[0]
	},
	getCallerData: () => {
		let callerData=[];
		for (let i= (GET_CALLERSDATA.data.length-1); i>=0;i--)
		{
				if(GET_CALLERSDATA.data[i].STATUS!="")
				{
					callerData.push(GET_CALLERSDATA.data[i])
				}
		}
		return callerData
	},
	get_install_n_leads_count:()=>
	{
		let data=0;

		for (let i=0; i<SELECT_LEADS.data.length; i++)
		{	
			if(SELECT_LEADS.data[i].REMARK=="INSTALL REQUEST"&& SELECT_LEADS.data[i].STATUS=="COMPLETE")
			{
				data++;
			}
		}
		return [data,SELECT_LEADS.data.length-data]
	},
	update_data:()=>{
			UPDATE_LEADS.run()
			INSERT_LOGS.run()
	},
	update_cust: ()=>{
		if(cust_Remark.selectedOptionLabel == "INSTALL REQUEST" && update_status.selectedOptionLabel == "COMPLETE"){
			UPDATE_CUSTOMER.run()
		}else{
			UPDATE_CUSTOMER.run()
			UPDATE_LOGS_CUST.run()
		}
	},
	active:()=>{
		if(log_remark.selectedOptionValue == "CALL BACK" || remark.selectedOptionValue == "CALL BACK"){
			return true;
		}else{
			return false;
		}
	}
}
