actionConfiguration.body:
() => {
  switch (appsmith.store.table_num) {
    case 0:
      if (appsmith.store.current_lead_card >= appsmith.store.table.length - 1) {
        SELECT_LEADS.run();
        storeValue("current_lead_card", 0);
        showAlert("Fresh leads have been updated", "success");
        return appsmith.store.current_lead_card;
      }
      storeValue("current_lead_card", appsmith.store.current_lead_card + 1);
      return appsmith.store.current_lead_card;
      break;
    case 1:
      if (appsmith.store.current_demo_card >= appsmith.store.table.length - 1) {
        SELECT_LEADS.run();
        storeValue("current_demo_card", 0);
        showAlert("Confirmed leads have been updated", "success");
        return appsmith.store.current_demo_card;
      }
      storeValue("current_demo_card", appsmith.store.current_demo_card + 1);
      return appsmith.store.current_demo_card;
      break;
    case 2:
      if (appsmith.store.current_call_back_card >= appsmith.store.table.length - 1) {
        SELECT_LEADS.run();
        storeValue("current_call_back_card", 0);
        showAlert("Followups have been updated", "success");
        return appsmith.store.current_call_back_card;
      }
      storeValue("current_call_back_card", appsmith.store.current_call_back_card + 1);
      return appsmith.store.current_call_back_card;
      break;
    case 3:
      showAlert("Data updated", "success");
      break;
    default:
      return Error();
  }
}

