actionConfiguration.body:
() => {
  if (progress.text == "NONE") {
    UPDATE_LEADS.run();
    INSERT_LOGS.run();
    return;
  } else if (progress.text == "LIVE DATA") {
    UPDATE_LEADS.run();
    INSERT_LOGS.run();
    return;
  } else if (progress.text == "INSTALL REQUEST") {
    UPDATE_LEADS.run();
    INSERT_LOGS.run();
    return;
  } else if (progress.text == "DEMO REQUEST") {
    if (log_remark.selectedOptionLabel == "INSTALL REQUEST") {
      UPDATE_LEADS.run();
      INSERT_LOGS.run();
      return;
    } else if (progress.text == "INTERESTED" || progress.text == "CALL BACK") {
      if (log_remark.selectedOptionLabel != "RNR" || log_remark.selectedOptionLabel != "WRONG NUM") {
        INSERT_LOGS.run();
        return;
      }
      UPDATE_LEADS.run();
      INSERT_LOGS.run();
      return;
    } else {
      INSERT_LOGS.run();
      return;
    }
  }
}

