actionConfiguration.body:
async () => {
  select_access_mode.run(() => {
    storeValue("access_type", NaN);
    for (let i = 0; i < select_access_mode.data.length; i++) {
      if (appsmith.user.username == select_access_mode.data[i].COMPANY_EMAIL) {
        let access = select_access_mode.data[i].ACCESS;
        storeValue("access_type", access);
        return;
      }
    }
  });
}

